INSERT INTO `wp_posts` VALUES("258", "3", "2013-10-16 11:17:51", "2013-10-16 09:17:51", "<p style=\"text-align: center;\">Over the years OIM has developed a unique, integrated approach to business performance management\r\nand strategy execution in these core areas. With a solid track record of working with blue-chip companies both locally and abroad,\r\nour multidisciplinary team eliminates the need to call in various business consultants.</p>\r\n<p style=\"text-align: center;\">Furthermore, our proven methodologies and way of working ensure that we can play an integral part\r\nin the continuous evolvement, progress and success of your business.</p>", "Home", "", "publish", "open", "open", "", "home", "", "", "2013-10-22 09:18:48", "2013-10-22 07:18:48", "", "0", "http://staging.fishgate.co.za/OIM/?page_id=258", "0", "page", "", "0");
