bout the rock drillers', '', 'inherit', 'open', 'open', '', '937-revision-v1', '', '', '2013-10-25 20:44:01', '2013-10-25 18:44:01', '', 937, 'http://staging.fishgate.co.za/OIM/news/uncategorized/937-revision-v1/', 0, 'revision', '', 0),
